mapper.select:
SELECT * FROM NQ
nq_category = #{nq_category}
ORDER BY nq_no desc

SELECT mbr_id, mbr_author, mbr_phone, mbr_status, mbr_name, mbr_nick, NVL((select count(*) from report_info group by mbr_id),0) as rcnt from member
mbr_author = #{mbr_author}
ORDER BY mbr_id

mapper.update:
update member set
mbr_author =
#{mbr_author}
where mbr_id = #{mbr_id}

update sitter set
sit_payday = #{sit_payday},
sit_off =  #{sit_off},
sit_pay =  #{sit_pay},
sit_loc = #{sit_loc},
sit_note = #{sit_note}

, sit_pic = #{sit_pic}

WHERE sit_mbr_id = #{sit_mbr_id}
