mapper.update:
UPDATE COMMUNITY SET 
COM_CATEGORY = #{com_category}, 
COM_TITLE = #{com_title},
COM_CONTENT = #{com_content}
WHERE COM_NO = #{com_no}

UPDATE COMMUNITY SET
COM_HIT = COM_HIT + 1
WHERE COM_NO = #{com_no}

mapper.select:
SELECT ROWNUM, S.SIT_MBR_ID, S.SIT_PAYDAY, replace(S.SIT_OFF, ' ', ', ') SIT_OFF, S.SIT_LOC, REGEXP_REPLACE(REVERSE(REGEXP_REPLACE( REVERSE(TO_CHAR(S.SIT_PAY)), '([0-9]{3})','\1,')), '^,','') AS SIT_PAY, S.SIT_AGE, S.SIT_NOTE, S.SIT_PIC
FROM SITTER S
where S.SIT_MBR_ID = #{sit_mbr_id}
