mapper.select:
	UPDATE

	MEMBER

	SET

    MBR_ACCOUNT= #{mbr_account}


	WHERE MBR_ID = #{mbr_id}

	UPDATE

	MEMBER

	SET

    MBR_STATUS= #{mbr_status}


	WHERE MBR_ID = #{mbr_id}

		SELECT rownum id, e.* 
		  FROM employees e


		       department_id = #{departmentId}


            and employee_id = #{employeeId}


             and salary < #{salary} 


            and upper(first_name) like '%' || upper(#{firstName}) || '%'


            and employee_id in  

                    #{id}



		 ORDER BY first_name
